({run: (() => {
  ethereumProvider.onMetaMaskEvent('newUnapprovedTx', txMeta => {
    let state = ethereumProvider.getPluginState()
    ethereumProvider.updatePluginState({  [txMeta.txParams.from]: (state[txMeta.txParams.from] || 0) + 1 })
    state = ethereumProvider.getPluginState()
    console.log('Number of transactions sent by address', JSON.stringify(state, null, 2));
  })
})
 })
